// ------------------------------------------------------------
// Third-Party Sources/Libraries/Algorithms
//
// ScareMail utilizes some third party libraries and 
// algorithms. 
//
// I use pattern.en and NLTK for natural language processing:
//
//   http://www.clips.ua.ac.be/pages/pattern-en
//   http://nltk.org
//
// I utilize Markov-chain style text generation as described by
// Kernighan and Pike in their book 'The Practice of Programming,'
// Copyright 1999, Lucent Technologies. (They allow use of their
// code for any purpose as long as the copyright appears here).
// I also owe thanks to Cheng Zhang for inspiring my Javascript
// translation of the Kernighan/Pike code.
//
//   http://cm.bell-labs.com/cm/cs/tpop/code.html
//
// Finally, for this and other recent projects, I have been
// using Brock Adams' waitForKeyElements():
//
//   https://gist.github.com/BrockA/2625891
// ------------------------------------------------------------


// ------------------------------------------------------------
// Text Sources
//
// ScareMail generates algorithmically unique stories each time
// it runs. These stories are several steps removed from an 
// original source, namely "Fahrenheit 451" by Ray Bradbury. 
//
// One of the primary transformations of that source text is 
// made by substituting its nouns and verbs from those found
// in a recently released Homeland Security Analyst document:
//
//   https://epic.org/foia/epic-v-dhs-media-monitoring/Analyst-Desktop-Binder-REDACTED.pdf
//
// Contained within this code resides several of these 
// transformations.
// ------------------------------------------------------------


var j;   // jQuery
var ELEMENT_POLL_SPEED = 500;  // element check interval in ms
var VERSION_NUMBER = '0.5';
var SCAREMAIL_HOME_URL= 'http://bengrosser.com/projects/scaremail/';
var GROSSER_URL= 'http://bengrosser.com/';

// obvious header option
var scaremailheader = 
"<br>--<br>"+
"Following Text Generated by <a href='http://bengrosser.com/projects/scaremail/'>"+
"ScareMail</a><br><br>";

var scarystory = "";


function main() {

    //console.log("ScareMail, ver. "+VERSION_NUMBER+"\n"+SCAREMAIL_HOME_URL);

    // setup jQuery on j to avoid any possible conflicts
    j = jQuery.noConflict();



    // watch for a new Gmail compose window, call addScaryStory() when found
    waitForKeyElements('.LW-avf', addScaryStory, false);
    waitForKeyElements('#gbzc', insertNavItem, true);

    // generates a new "scary" story and adds it to the email signature (if not
    // already present)
    function addScaryStory(jnode) {

        // checks to see if the scaremailheader already exists, if it doesn't,
        // generates a new scary story and adds to the email signature
        if(!jnode.text().contains("Following Text Generated by ScareMail")) {
            var storylength = getRandomInt(400,1000);
            scarystory = getScaryStory(storylength);
            jnode.append('<div>'+scaremailheader+scarystory+'</div>');
        }
    }

}

function insertNavItem() {

    // insert nav menu item for ScareMail
    if(j('.scaremailnav').length == 0) {
        j('#gbzc').append('<li class="gbt scaremailnav"><a id="scaremaillink" class="gbzt"><span class="gbtb2"></span><span class="gbts">ScareMail</span></a></li>')
    }

        // Facebook Like Button for the Demetricator Project Homepage
    var likebutton = 
    '<iframe id="fbd_like_button" src="//www.facebook.com/plugins/like.php?href='+SCAREMAIL_HOME_URL+'&amp;send=false&amp;layout=button_count&amp;width=90&amp;show_faces=false&amp;action=like&amp;colorscheme=light&amp;font&amp;height=21&amp;" scrolling="no" frameborder="0" style="border:none; overflow:hidden; width:90px; height:21px;margin:10px 0px 10px 0px;" allowTransparency="true"></iframe>';

     // HTML for the dialog box
    var dialoghtml = 
        '<div style="display:none; width: 330px; height: 230px; margin: 0px auto; background-color: white;" class="" id="modaldialog">'+
        '<div style="margin:5px;" id="modalheader"> <label style="margin:5px;float:right;" class="simplemodal-close">X</label><br><br> </div> <center> <span style="font-size:16px;font-weight:bold;padding:10px 0;"><a class="gbmlb" href="'+SCAREMAIL_HOME_URL+'" target="_blank">ScareMail</a></span> <span  style="padding:8px;font-size:80%;" class="gbps2">Makes email "scary" to disrupt NSA surveillance</span><br/>'
        +likebutton+
        '<div style="width:100%;"><span class="" style="font-size:14px;font-weight:normal">by</span> Benjamin Grosser</div> <span style="font-size:14px;" class=""><a href="'+GROSSER_URL+'" target="_blank">bengrosser.com</a></span> </center> <div id="modalfooter" style="margin:15px;"> <p style="float:left;font-size:10px;" class="">version '+VERSION_NUMBER+'</p> <p style="font-size:10px;float:right;" class=""><a href="'+SCAREMAIL_HOME_URL+'" target="_blank">More info/feedback...</a></p> </div> </div>';



    j('body').append(dialoghtml);

    j('#scaremaillink').click(function() {
        console.log("clicked on scaremail link");
        j('#modaldialog').modal({
            opacity:65,
            overlayClose:true,
            overlayCss: {backgroundColor:"#000"}
        });
    });
}


// Markov-Chain Text Generation
var prefix;
var dict;
var order = 2;

function initprefix() {
    prefix = new Array(order);
    for (var i = 0; i < order; i++) prefix[i] = " ";
}

function add(s) {
    key = prefix.join("#");
    if (dict[key] == null) dict[key] = new Array();
    dict[key].push(s);
    prefix.shift();
    prefix.push(s);
}

function gen(n) {
    initprefix();

    var out = "";
    for (var i = 0; i < n; i++) {
        var words = dict[ prefix.join("#") ];
        var word = choice(words);
        if (word == " ") break;
        out += word + " "
        prefix.shift();
        prefix.push(word);
    }

    return out;
}

function choice(choices) {
    index = Math.floor(Math.random() * choices.length);
    return choices[index];
}

function getScaryStory(numwords) {

    //var inp = newfull;
    //var inp = brfull;
    //var inp = newtst6;
    var inp = newswap;

    // split text into tokens
    inp = inp.split(" ");

    initprefix();
    dict = new Array();

    for (var i = 0; i < inp.length; i++) 
        add(inp[i]);

    //console.log(dict);
    add(" ");

    return gen(numwords);
}

// Utility
//
//

String.prototype.contains = function(it) { return this.indexOf(it) != -1; };

function getRandomInt (min, max) {
    return Math.floor(Math.random() * (max - min + 1)) + min;
}

function waitForKeyElements (selectorTxt, actionFunction, bWaitOnce, iframeSelector ) {

    var targetNodes, btargetsFound;

    targetNodes = j(selectorTxt);

    if (targetNodes  &&  targetNodes.length > 0) {
        btargetsFound   = true;

        // found target node(s).  go through each and act if they are new.
        targetNodes.each ( function () {
            var jThis        = j(this);
            var alreadyFound = jThis.data ('alreadyFound')  ||  false;

            if (!alreadyFound) {
                // call the payload function.
                //unsafeWindow.console.log("waitFor got a new element: "+selectorTxt);
                var cancelFound = actionFunction (jThis);
                if (cancelFound) btargetsFound = false;
                else jThis.data ('alreadyFound', true);
            }
        } );
    }

    else {
        btargetsFound   = false;
    }

    // get the timer-control variable for this selector.
    var controlObj      = waitForKeyElements.controlObj  ||  {};
    var controlKey      = selectorTxt.replace (/[^\w]/g, "_");
    var timeControl     = controlObj [controlKey];

    // now set or clear the timer as appropriate.
    if (btargetsFound && bWaitOnce && timeControl) {
        // the only condition where we need to clear the timer.
        clearInterval (timeControl);
        delete controlObj [controlKey]
    }

    else {
        // set a timer, if needed.
        if (!timeControl) {
            timeControl = setInterval ( function () {
                waitForKeyElements(selectorTxt, actionFunction, bWaitOnce, iframeSelector);
                }, ELEMENT_POLL_SPEED
            );

            controlObj [controlKey] = timeControl;
        }
    }

    waitForKeyElements.controlObj = controlObj;
    
}


